SELECT object_construct('x', "CENTER", 'y', "COUNT(BUCKET-IDX)") FROM ( SELECT  *  FROM ( SELECT "CENTER", count("bucket-idx") AS "COUNT(BUCKET-IDX)" FROM ( SELECT *, ((("bucket-idx" * 20.0 :: FLOAT) + 10.0 :: FLOAT) + 0.0 :: FLOAT) AS "CENTER" FROM ( SELECT *, floor(("truncated-value" / 20.0 :: FLOAT)) AS "bucket-idx" FROM ( SELECT *, iff(("MET['PT']" < 0.0 :: FLOAT), (0.0 :: FLOAT - 10.0 :: FLOAT), iff(("MET['PT']" > 2000.0 :: FLOAT), (2000.0 :: FLOAT + 10.0 :: FLOAT), ("MET['PT']" - 0.0 :: FLOAT))) AS "truncated-value" FROM ( SELECT * FROM ( SELECT MET['pt'] FROM ( SELECT  *  FROM (adl))))))) GROUP BY "CENTER") ORDER BY "CENTER" ASC NULLS FIRST);
